SELECT SUM("movie_keyword"."movie_id") as agg_0 FROM "movie_keyword" LEFT OUTER JOIN "title" ON "movie_keyword"."movie_id" = "title"."id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "company_type" ON "movie_companies"."company_type_id" = "company_type"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id"  WHERE "movie_companies"."company_type_id" BETWEEN 1 AND 2 AND "cast_info"."note" LIKE '%(voice)%' AND "kind_type"."kind" LIKE '%tv%' AND "movie_companies"."note" LIKE '%(20%01)%' AND "movie_info_idx"."info_type_id" != 100 AND "company_type"."kind" LIKE '%companies%' AND "movie_info"."movie_id" IS NOT NULL;