SELECT SUM("movie_info_idx"."info_type_id") as agg_0 FROM "title" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "info_type" ON "movie_info_idx"."info_type_id" = "info_type"."id"  WHERE ("info_type"."id" <= 28 OR "info_type"."id" BETWEEN 39 AND 106) AND "title"."imdb_index" != '%XXI%' AND "info_type"."info" NOT LIKE '%L%D%' AND "movie_companies"."movie_id" IS NOT NULL;