SELECT MAX("movie_info"."id") as agg_0 FROM "char_name" LEFT OUTER JOIN "cast_info" ON "char_name"."id" = "cast_info"."person_role_id" LEFT OUTER JOIN "aka_name" ON "cast_info"."person_id" = "aka_name"."id" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "name" ON "aka_name"."person_id" = "name"."id" LEFT OUTER JOIN "info_type" ON "movie_info_idx"."info_type_id" = "info_type"."id" LEFT OUTER JOIN "person_info" ON "name"."id" = "person_info"."person_id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id"  WHERE "info_type"."info" IN ('LD quality of source', 'LD sharpness', 'LD status of availablility', 'LD supplement', 'runtimes') AND "title"."title" NOT LIKE '%de%' AND "movie_companies"."company_type_id" <= 1 AND "movie_info"."info" LIKE '%Japanese%' AND "movie_companies"."note" NOT LIKE '%airin%g)%' AND "person_info"."person_id" IS NOT NULL;