SELECT AVG("title"."production_year") as agg_0, COUNT(*) as agg_1 FROM "keyword" LEFT OUTER JOIN "movie_keyword" ON "keyword"."id" = "movie_keyword"."keyword_id" LEFT OUTER JOIN "title" ON "movie_keyword"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "info_type" ON "movie_info_idx"."info_type_id" = "info_type"."id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id"  WHERE "cast_info"."nr_order" BETWEEN 5.479748018058393 AND 867092485.0250335 AND "cast_info"."person_id" >= 1622631 AND "movie_info_idx"."id" BETWEEN 862870 AND 931568 AND "info_type"."id" IS NOT NULL;