SELECT SUM("movie_info_idx"."info_type_id") as agg_0 FROM "title" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "info_type" ON "movie_info_idx"."info_type_id" = "info_type"."id"  WHERE "movie_info_idx"."movie_id" >= 1291265 AND "kind_type"."kind" != '%tv series%' AND "title"."title" LIKE '%and%' AND "info_type"."info" NOT LIKE '%d%ate%' AND "kind_type"."kind" LIKE '%video%' AND "movie_info_idx"."info_type_id" >= 99 AND "movie_keyword"."movie_id" IS NOT NULL;