SELECT MAX("title"."episode_of_id") as agg_0, MAX("movie_companies"."id") as agg_1 FROM "info_type" LEFT OUTER JOIN "movie_info_idx" ON "info_type"."id" = "movie_info_idx"."info_type_id" LEFT OUTER JOIN "title" ON "movie_info_idx"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id"  WHERE "movie_companies"."note" LIKE '%(2002)%' AND "info_type"."info" NOT LIKE '%disc%' AND "movie_companies"."company_type_id" BETWEEN 1 AND 2 AND "title"."season_nr" IS NULL AND ("movie_info_idx"."id" >= 57249 OR "movie_info_idx"."info" NOT LIKE '%7.4%' OR "movie_info_idx"."id" BETWEEN 902192 AND 1024153);