SELECT AVG("title"."episode_of_id") as agg_0, COUNT(*) as agg_1 FROM "info_type" LEFT OUTER JOIN "movie_info_idx" ON "info_type"."id" = "movie_info_idx"."info_type_id" LEFT OUTER JOIN "title" ON "movie_info_idx"."movie_id" = "title"."id"  WHERE "movie_info_idx"."id" >= 47644 AND "movie_info_idx"."info" LIKE '%6%.9%' AND "movie_info_idx"."movie_id" BETWEEN 894453 AND 2062777 AND "info_type"."id" >= 2;