SELECT SUM("kind_type"."id") as agg_0, COUNT(*) as agg_1 FROM "kind_type" LEFT OUTER JOIN "title" ON "kind_type"."id" = "title"."kind_id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id" LEFT OUTER JOIN "keyword" ON "movie_keyword"."keyword_id" = "keyword"."id"  WHERE "title"."title" NOT LIKE '%t%he%' AND "movie_companies"."company_type_id" != 2 AND "kind_type"."id" BETWEEN 2 AND 6 AND "kind_type"."kind" IN ('tv mini series', 'video movie', 'video game', 'tv series', 'tv movie') AND "keyword"."id" IS NOT NULL;