SELECT AVG("title"."season_nr") as agg_0, SUM("title"."kind_id") as agg_1 FROM "char_name" LEFT OUTER JOIN "cast_info" ON "char_name"."id" = "cast_info"."person_role_id" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "company_type" ON "movie_companies"."company_type_id" = "company_type"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "company_name" ON "movie_companies"."company_id" = "company_name"."id"  WHERE "movie_companies"."note" LIKE '%(%all%' AND "company_type"."id" != 1 AND "title"."title" LIKE '%to%' AND "kind_type"."id" IS NOT NULL AND "movie_info"."movie_id" IS NOT NULL AND "company_name"."id" IS NOT NULL;