SELECT COUNT(*) as agg_0, COUNT(*) as agg_1 FROM "movie_keyword" LEFT OUTER JOIN "title" ON "movie_keyword"."movie_id" = "title"."id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "char_name" ON "cast_info"."person_role_id" = "char_name"."id"  WHERE ("kind_type"."id" != 5 OR ("kind_type"."id" <= 3 AND "kind_type"."id" BETWEEN 1 AND 3)) AND "title"."title" NOT LIKE '%d%e%' AND ("char_name"."imdb_index" IN ('II') OR ("char_name"."name" NOT LIKE '%the%' AND "char_name"."imdb_index" != '%IV%')) AND "kind_type"."kind" != '%tv movie%';