SELECT SUM("company_name"."id") as agg_0, AVG("movie_companies"."company_type_id") as agg_1 FROM "company_type" LEFT OUTER JOIN "movie_companies" ON "company_type"."id" = "movie_companies"."company_type_id" LEFT OUTER JOIN "company_name" ON "movie_companies"."company_id" = "company_name"."id"  WHERE ("company_type"."id" >= 3 OR "company_type"."kind" IN ('special effects companies', 'production companies', 'distributors') OR "company_type"."id" BETWEEN 3 AND 4) AND "movie_companies"."company_id" >= 14831 AND "company_name"."name" NOT LIKE '%Video%' AND "company_name"."country_code" NOT LIKE '%[gb]%' AND "movie_companies"."movie_id" <= 1754288;