SELECT AVG("title"."episode_of_id") as agg_0, SUM("title"."episode_nr") as agg_1 FROM "movie_keyword" LEFT OUTER JOIN "title" ON "movie_keyword"."movie_id" = "title"."id" LEFT OUTER JOIN "keyword" ON "movie_keyword"."keyword_id" = "keyword"."id"  WHERE "title"."title" NOT LIKE '%to%' AND "movie_keyword"."keyword_id" <= 7143 AND "title"."imdb_index" != '%XII%' AND "keyword"."phonetic_code" LIKE '%R%1652%';