SELECT SUM("cast_info"."person_id") as agg_0, MAX("cast_info"."role_id") as agg_1 FROM "aka_name" LEFT OUTER JOIN "cast_info" ON "aka_name"."id" = "cast_info"."person_id" LEFT OUTER JOIN "name" ON "aka_name"."person_id" = "name"."id"  WHERE "cast_info"."note" NOT LIKE '%Adler%)%' AND "name"."imdb_index" LIKE '%I%V%' AND "name"."name_pcode_cf" NOT LIKE '%A5%362%' AND "aka_name"."name_pcode_cf" NOT LIKE '%A53%62%' AND "aka_name"."surname_pcode" IS NOT NULL AND ("name"."id" >= 1256117 OR "name"."gender" = '%f%' OR "name"."id" >= 295938);