SELECT SUM("company_type"."id") as agg_0 FROM "info_type" LEFT OUTER JOIN "movie_info_idx" ON "info_type"."id" = "movie_info_idx"."info_type_id" LEFT OUTER JOIN "title" ON "movie_info_idx"."movie_id" = "title"."id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "company_type" ON "movie_companies"."company_type_id" = "company_type"."id" LEFT OUTER JOIN "company_name" ON "movie_companies"."company_id" = "company_name"."id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id"  WHERE "company_type"."id" BETWEEN 2 AND 3 AND ("kind_type"."kind" != '%tv series%' OR "kind_type"."kind" LIKE '%s%eries%' OR "kind_type"."kind" = '%episode%') AND ("movie_info_idx"."movie_id" BETWEEN 283123 AND 1869049 OR ("movie_info_idx"."info" LIKE '%7.%8%' AND "movie_info_idx"."movie_id" >= 2407605)) AND "movie_companies"."note" LIKE '%(2008)%' AND "company_type"."kind" LIKE '%compan%ies%' AND "movie_info"."movie_id" IS NOT NULL AND "company_name"."id" IS NOT NULL AND "movie_keyword"."movie_id" IS NOT NULL;