SELECT AVG("title"."episode_of_id") as agg_0 FROM "name" LEFT OUTER JOIN "aka_name" ON "name"."id" = "aka_name"."person_id" LEFT OUTER JOIN "cast_info" ON "aka_name"."id" = "cast_info"."person_id" LEFT OUTER JOIN "person_info" ON "name"."id" = "person_info"."person_id" LEFT OUTER JOIN "char_name" ON "cast_info"."person_role_id" = "char_name"."id" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "company_name" ON "movie_companies"."company_id" = "company_name"."id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id"  WHERE "name"."name" LIKE '%Anderson,%' AND "aka_name"."name_pcode_cf" LIKE '%A536%2%' AND "company_name"."country_code" NOT LIKE '%[e%s]%' AND ("aka_name"."id" >= 259096 OR "aka_name"."surname_pcode" LIKE '%B2%') AND "person_info"."person_id" IS NOT NULL AND "char_name"."id" IS NOT NULL AND "movie_info"."movie_id" IS NOT NULL AND "kind_type"."id" IS NOT NULL AND "movie_keyword"."movie_id" IS NOT NULL;