SELECT COUNT(*) as agg_0 FROM "kind_type" LEFT OUTER JOIN "title" ON "kind_type"."id" = "title"."kind_id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id"  WHERE "movie_info_idx"."id" >= 1203621 AND "kind_type"."kind" NOT LIKE '%video%' AND "kind_type"."id" <= 2 AND "movie_companies"."note" LIKE '%(Hun%gary)%' AND "title"."title" NOT LIKE '%i%n%' AND "movie_keyword"."movie_id" IS NOT NULL;