SELECT MAX("cast_info"."id") as agg_0, MAX("movie_info_idx"."movie_id") as agg_1 FROM "movie_info_idx" LEFT OUTER JOIN "title" ON "movie_info_idx"."movie_id" = "title"."id" LEFT OUTER JOIN "info_type" ON "movie_info_idx"."info_type_id" = "info_type"."id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "company_type" ON "movie_companies"."company_type_id" = "company_type"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id" LEFT OUTER JOIN "keyword" ON "movie_keyword"."keyword_id" = "keyword"."id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id"  WHERE "movie_info_idx"."info_type_id" >= 100 AND "movie_info"."id" BETWEEN 5749606 AND 6195615 AND "info_type"."id" BETWEEN 18 AND 90 AND "movie_info"."info" LIKE '%English%' AND "title"."title" LIKE '%an%d%' AND ("company_type"."id" != 2 OR "company_type"."kind" NOT LIKE '%companie%s%') AND "kind_type"."id" IS NOT NULL AND "keyword"."id" IS NOT NULL;