SELECT MAX("title"."id") as agg_0, MAX("aka_name"."person_id") as agg_1 FROM "cast_info" LEFT OUTER JOIN "char_name" ON "cast_info"."person_role_id" = "char_name"."id" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "info_type" ON "movie_info_idx"."info_type_id" = "info_type"."id" LEFT OUTER JOIN "aka_name" ON "cast_info"."person_id" = "aka_name"."id"  WHERE "movie_info"."info" LIKE '%Portuguese%' AND "info_type"."info" != '%magazine cover photo%' AND "title"."kind_id" BETWEEN 2 AND 7 AND "char_name"."id" IS NOT NULL AND "movie_keyword"."movie_id" IS NOT NULL AND "kind_type"."id" IS NOT NULL;