SELECT COUNT(*) as agg_0 FROM "movie_keyword" LEFT OUTER JOIN "title" ON "movie_keyword"."movie_id" = "title"."id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id"  WHERE "title"."title" LIKE '%and%' AND "kind_type"."id" != 3 AND "title"."kind_id" <= 7 AND "kind_type"."kind" NOT LIKE '%movie%' AND ("kind_type"."kind" LIKE '%tv%' OR "kind_type"."kind" NOT LIKE '%tv%');