SELECT COUNT(*) as agg_0, SUM("title"."season_nr") as agg_1 FROM "cast_info" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id" LEFT OUTER JOIN "char_name" ON "cast_info"."person_role_id" = "char_name"."id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "company_name" ON "movie_companies"."company_id" = "company_name"."id" LEFT OUTER JOIN "company_type" ON "movie_companies"."company_type_id" = "company_type"."id"  WHERE "char_name"."imdb_index" IN ('M', 'DM', 'II', 'IV') AND "char_name"."name_pcode_nf" NOT LIKE '%H52%41%' AND "company_type"."kind" != '%special effects companies%' AND "kind_type"."id" IS NOT NULL AND "movie_info_idx"."movie_id" IS NOT NULL AND "movie_info"."movie_id" IS NOT NULL AND "company_name"."id" IS NOT NULL;