SELECT SUM("movie_companies"."company_type_id") as agg_0 FROM "movie_info" LEFT OUTER JOIN "title" ON "movie_info"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id"  WHERE "movie_info"."info" LIKE '%Span%ish%' AND "title"."kind_id" != 1 AND "title"."title" LIKE '%in%' AND ("title"."id" BETWEEN 1873852 AND 1962119 OR "title"."imdb_index" IN ('VI', 'IV', 'VII', 'I', 'VIII', 'IX', 'V', 'III', 'X')) AND "movie_info"."movie_id" <= 2104375 AND "movie_companies"."company_type_id" <= 2;