SELECT MIN("title"."production_year") as agg_0 FROM "keyword" LEFT OUTER JOIN "movie_keyword" ON "keyword"."id" = "movie_keyword"."keyword_id" LEFT OUTER JOIN "title" ON "movie_keyword"."movie_id" = "title"."id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id"  WHERE "keyword"."id" <= 131884 AND "keyword"."phonetic_code" LIKE '%R1%652%' AND ("kind_type"."id" BETWEEN 3 AND 4 OR "kind_type"."id" >= 3 OR "kind_type"."id" != 3) AND "title"."kind_id" >= 1 AND "movie_keyword"."keyword_id" <= 94431;