SELECT COUNT(*) as agg_0, SUM("movie_info"."id") as agg_1 FROM "cast_info" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "char_name" ON "cast_info"."person_role_id" = "char_name"."id" LEFT OUTER JOIN "aka_name" ON "cast_info"."person_id" = "aka_name"."id" LEFT OUTER JOIN "name" ON "aka_name"."person_id" = "name"."id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id"  WHERE "movie_keyword"."movie_id" <= 128875 AND ("aka_name"."name" NOT LIKE '%Dr.%' OR "aka_name"."surname_pcode" LIKE '%B%63%') AND "char_name"."name_pcode_nf" LIKE '%H52%41%' AND "movie_companies"."movie_id" IS NOT NULL AND "movie_info_idx"."movie_id" IS NOT NULL;