SELECT MAX("movie_info"."info_type_id") as agg_0, COUNT(*) as agg_1 FROM "keyword" LEFT OUTER JOIN "movie_keyword" ON "keyword"."id" = "movie_keyword"."keyword_id" LEFT OUTER JOIN "title" ON "movie_keyword"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id"  WHERE ("movie_keyword"."keyword_id" BETWEEN 132 AND 22971 OR "movie_keyword"."movie_id" <= 1730614 OR "movie_keyword"."keyword_id" <= 6417) AND "movie_info"."info" NOT LIKE '%Span%ish%' AND "title"."imdb_index" != '%IV%' AND "movie_keyword"."id" <= 733346;