SELECT AVG("movie_companies"."movie_id") as agg_0 FROM "title" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "company_type" ON "movie_companies"."company_type_id" = "company_type"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id"  WHERE "company_type"."kind" != '%production companies%' AND "title"."title" LIKE '%to%' AND "title"."series_years" != '%1967-1996%' AND "movie_info"."movie_id" IS NOT NULL;