SELECT MAX("title"."episode_of_id") as agg_0 FROM "kind_type" LEFT OUTER JOIN "title" ON "kind_type"."id" = "title"."kind_id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "info_type" ON "movie_info_idx"."info_type_id" = "info_type"."id"  WHERE "kind_type"."kind" NOT LIKE '%vide%o%' AND ("kind_type"."kind" NOT LIKE '%video%' OR ("kind_type"."id" != 2 AND "kind_type"."kind" NOT LIKE '%video%')) AND "title"."title" LIKE '%and%' AND "title"."season_nr" <= 1.0 AND "info_type"."id" IS NOT NULL;