SELECT MAX("kind_type"."id") as agg_0 FROM "movie_info" LEFT OUTER JOIN "title" ON "movie_info"."movie_id" = "title"."id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id"  WHERE "movie_info"."info" NOT LIKE '%Portuguese%' AND "kind_type"."kind" NOT LIKE '%vi%deo%' AND "kind_type"."kind" != '%movie%' AND "movie_info_idx"."info" NOT LIKE '%7.%2%';