SELECT MAX("cast_info"."person_id") as agg_0, MAX("movie_keyword"."movie_id") as agg_1 FROM "aka_name" LEFT OUTER JOIN "cast_info" ON "aka_name"."id" = "cast_info"."person_id" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id" LEFT OUTER JOIN "char_name" ON "cast_info"."person_role_id" = "char_name"."id"  WHERE "movie_keyword"."movie_id" BETWEEN 660993 AND 1740928 AND "aka_name"."person_id" >= 1258100 AND "char_name"."surname_pcode" LIKE '%M5%';