SELECT MIN("aka_name"."person_id") as agg_0, COUNT(*) as agg_1 FROM "person_info" LEFT OUTER JOIN "name" ON "person_info"."person_id" = "name"."id" LEFT OUTER JOIN "aka_name" ON "name"."id" = "aka_name"."person_id" LEFT OUTER JOIN "cast_info" ON "aka_name"."id" = "cast_info"."person_id"  WHERE "cast_info"."person_id" >= 545801 AND "aka_name"."surname_pcode" NOT LIKE '%B%6%' AND "aka_name"."imdb_index" IN ('III', 'II');