SELECT SUM("company_type"."id") as agg_0, COUNT(*) as agg_1 FROM "kind_type" LEFT OUTER JOIN "title" ON "kind_type"."id" = "title"."kind_id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "company_name" ON "movie_companies"."company_id" = "company_name"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "company_type" ON "movie_companies"."company_type_id" = "company_type"."id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id"  WHERE "kind_type"."kind" LIKE '%vid%eo%' AND "company_type"."kind" LIKE '%companies%' AND "title"."title" LIKE '%a%nd%' AND "movie_info"."info" LIKE '%Dutch%' AND "company_name"."name" NOT LIKE '%Home%' AND "movie_info_idx"."info" LIKE '%7.2%';