SELECT MAX("cast_info"."person_role_id") as agg_0, COUNT(*) as agg_1 FROM "movie_info_idx" LEFT OUTER JOIN "title" ON "movie_info_idx"."movie_id" = "title"."id" LEFT OUTER JOIN "info_type" ON "movie_info_idx"."info_type_id" = "info_type"."id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id" LEFT OUTER JOIN "aka_name" ON "cast_info"."person_id" = "aka_name"."id" LEFT OUTER JOIN "name" ON "aka_name"."person_id" = "name"."id" LEFT OUTER JOIN "keyword" ON "movie_keyword"."keyword_id" = "keyword"."id"  WHERE "aka_name"."name_pcode_cf" NOT LIKE '%A536%2%' AND "keyword"."id" <= 75662 AND "name"."surname_pcode" IN ('L', 'L2', 'M6', 'M2', 'M62') AND "info_type"."id" IS NOT NULL;