SELECT COUNT(*) as agg_0, AVG("title"."id") as agg_1 FROM "kind_type" LEFT OUTER JOIN "title" ON "kind_type"."id" = "title"."kind_id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id"  WHERE ("movie_info_idx"."info" LIKE '%7.%7%' OR "movie_info_idx"."info" NOT LIKE '%7.8%' OR "movie_info_idx"."info" LIKE '%7.%1%') AND "title"."title" LIKE '%to%' AND "movie_info_idx"."info_type_id" = 99;