SELECT SUM("name"."id") as agg_0 FROM "person_info" LEFT OUTER JOIN "name" ON "person_info"."person_id" = "name"."id" LEFT OUTER JOIN "aka_name" ON "name"."id" = "aka_name"."person_id" LEFT OUTER JOIN "cast_info" ON "aka_name"."id" = "cast_info"."person_id"  WHERE "person_info"."person_id" <= 622982 AND "aka_name"."person_id" >= 1668804 AND "name"."name_pcode_cf" NOT LIKE '%A4253%' AND ("name"."id" <= 3440660 OR "name"."imdb_index" LIKE '%IV%') AND "name"."name" LIKE '%John%' AND "cast_info"."note" NOT LIKE '%(as%';