SELECT AVG("title"."id") as agg_0 FROM "cast_info" LEFT OUTER JOIN "aka_name" ON "cast_info"."person_id" = "aka_name"."id" LEFT OUTER JOIN "char_name" ON "cast_info"."person_role_id" = "char_name"."id" LEFT OUTER JOIN "name" ON "aka_name"."person_id" = "name"."id" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id"  WHERE "name"."surname_pcode" IN ('L', 'S53', 'L2', 'J52', 'B65', 'M2', 'R2', 'M6') AND "name"."name_pcode_cf" NOT LIKE '%A53%62%' AND "char_name"."id" >= 1130989 AND "movie_companies"."movie_id" IS NOT NULL AND "kind_type"."id" IS NOT NULL;