SELECT MAX("movie_companies"."movie_id") as agg_0 FROM "movie_keyword" LEFT OUTER JOIN "title" ON "movie_keyword"."movie_id" = "title"."id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id"  WHERE "movie_keyword"."id" >= 675613 AND "movie_companies"."note" LIKE '%a%iring)%' AND "kind_type"."kind" != '%episode%' AND "title"."title" NOT LIKE '%i%n%' AND "kind_type"."id" <= 3;