SELECT MAX("title"."production_year") as agg_0, MAX("title"."production_year") as agg_1 FROM "company_name" LEFT OUTER JOIN "movie_companies" ON "company_name"."id" = "movie_companies"."company_id" LEFT OUTER JOIN "title" ON "movie_companies"."movie_id" = "title"."id"  WHERE ("title"."title" NOT LIKE '%to%' OR "title"."production_year" <= 2006.1620089062176) AND "title"."imdb_index" != '%XIV%' AND "movie_companies"."company_type_id" BETWEEN 2 AND 3 AND "movie_companies"."note" NOT LIKE '%(Spain)%';