SELECT MAX("cast_info"."movie_id") as agg_0, MIN("cast_info"."person_id") as agg_1 FROM "company_name" LEFT OUTER JOIN "movie_companies" ON "company_name"."id" = "movie_companies"."company_id" LEFT OUTER JOIN "title" ON "movie_companies"."movie_id" = "title"."id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id"  WHERE "movie_companies"."note" NOT LIKE '%(Hungary)%' AND "title"."title" NOT LIKE '%T%he%' AND "title"."imdb_index" IN ('III', 'I', 'IV', 'II', 'IX', 'VI', 'X', 'VII') AND "company_name"."id" <= 120699 AND "title"."series_years" != '%1983-1998%';