SELECT MAX("title"."production_year") as agg_0, COUNT(*) as agg_1 FROM "aka_name" LEFT OUTER JOIN "cast_info" ON "aka_name"."id" = "cast_info"."person_id" LEFT OUTER JOIN "char_name" ON "cast_info"."person_role_id" = "char_name"."id" LEFT OUTER JOIN "name" ON "aka_name"."person_id" = "name"."id" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "info_type" ON "movie_info_idx"."info_type_id" = "info_type"."id" LEFT OUTER JOIN "keyword" ON "movie_keyword"."keyword_id" = "keyword"."id" LEFT OUTER JOIN "person_info" ON "name"."id" = "person_info"."person_id"  WHERE "cast_info"."note" NOT LIKE '%Adler)%' AND "kind_type"."kind" IN ('video movie') AND ("title"."title" NOT LIKE '%in%' OR "title"."title" LIKE '%Part%' OR "title"."title" LIKE '%t%o%') AND "char_name"."surname_pcode" NOT LIKE '%M5%' AND "info_type"."id" IS NOT NULL AND "keyword"."id" IS NOT NULL AND "person_info"."person_id" IS NOT NULL;