SELECT COUNT(*) as agg_0, MAX("movie_keyword"."keyword_id") as agg_1 FROM "title" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id"  WHERE "movie_info_idx"."movie_id" <= 1201094 AND "kind_type"."kind" LIKE '%serie%s%' AND ("movie_keyword"."keyword_id" <= 87931 OR ("movie_keyword"."keyword_id" >= 474 AND "movie_keyword"."keyword_id" <= 621)) AND "title"."production_year" <= 2007.513368774006;