SELECT AVG("cast_info"."person_role_id") as agg_0 FROM "info_type" LEFT OUTER JOIN "movie_info_idx" ON "info_type"."id" = "movie_info_idx"."info_type_id" LEFT OUTER JOIN "title" ON "movie_info_idx"."movie_id" = "title"."id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id" LEFT OUTER JOIN "char_name" ON "cast_info"."person_role_id" = "char_name"."id"  WHERE "movie_info_idx"."info" NOT LIKE '%7.4%' AND ("char_name"."id" >= 2787680 OR "char_name"."name" LIKE '%t%he%') AND "title"."episode_of_id" <= 805010.3413276117;