SELECT AVG("company_type"."id") as agg_0, COUNT(*) as agg_1 FROM "company_name" LEFT OUTER JOIN "movie_companies" ON "company_name"."id" = "movie_companies"."company_id" LEFT OUTER JOIN "company_type" ON "movie_companies"."company_type_id" = "company_type"."id" LEFT OUTER JOIN "title" ON "movie_companies"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id"  WHERE ("movie_keyword"."id" >= 823069 OR "movie_keyword"."keyword_id" BETWEEN 6014 AND 8287) AND "movie_info_idx"."info" LIKE '%7%.8%' AND "movie_info_idx"."id" <= 892871;