SELECT AVG("cast_info"."person_role_id") as agg_0, AVG("cast_info"."nr_order") as agg_1 FROM "name" LEFT OUTER JOIN "person_info" ON "name"."id" = "person_info"."person_id" LEFT OUTER JOIN "aka_name" ON "name"."id" = "aka_name"."person_id" LEFT OUTER JOIN "cast_info" ON "aka_name"."id" = "cast_info"."person_id" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "info_type" ON "movie_info_idx"."info_type_id" = "info_type"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id"  WHERE ("person_info"."info" NOT LIKE '%D%avid%' OR "person_info"."info" LIKE '%i%n%') AND "movie_info"."id" BETWEEN 2545972 AND 11484956 AND "movie_info_idx"."info" LIKE '%7.%7%' AND "kind_type"."kind" NOT LIKE '%mo%vie%' AND "info_type"."id" <= 85 AND "aka_name"."name_pcode_cf" NOT LIKE '%A5%362%' AND "movie_companies"."movie_id" IS NOT NULL;