SELECT AVG("movie_info"."movie_id") as agg_0 FROM "movie_info_idx" LEFT OUTER JOIN "info_type" ON "movie_info_idx"."info_type_id" = "info_type"."id" LEFT OUTER JOIN "title" ON "movie_info_idx"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id"  WHERE "movie_info"."info_type_id" <= 3 AND "title"."series_years" IN ('2012-????') AND "info_type"."info" NOT LIKE '%s%ound%' AND "title"."title" LIKE '%i%n%';