SELECT AVG("title"."production_year") as agg_0, AVG("movie_companies"."company_type_id") as agg_1 FROM "aka_name" LEFT OUTER JOIN "cast_info" ON "aka_name"."id" = "cast_info"."person_id" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id"  WHERE "aka_name"."name" NOT LIKE '%D%avid%' AND "movie_companies"."note" LIKE '%(al%l%' AND "movie_companies"."movie_id" <= 1370651;