SELECT MIN("movie_info"."id") as agg_0, AVG("movie_info"."id") as agg_1 FROM "movie_keyword" LEFT OUTER JOIN "title" ON "movie_keyword"."movie_id" = "title"."id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id" LEFT OUTER JOIN "char_name" ON "cast_info"."person_role_id" = "char_name"."id" LEFT OUTER JOIN "aka_name" ON "cast_info"."person_id" = "aka_name"."id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "company_type" ON "movie_companies"."company_type_id" = "company_type"."id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "keyword" ON "movie_keyword"."keyword_id" = "keyword"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id"  WHERE "title"."episode_nr" BETWEEN 10.528576782248301 AND 13.640548626218154 AND ("company_type"."kind" IN ('distributors', 'production companies', 'miscellaneous companies') OR "company_type"."kind" LIKE '%c%ompanies%' OR "company_type"."kind" NOT LIKE '%companies%') AND "cast_info"."nr_order" BETWEEN 7.701263827821808 AND 1089526908.6810095 AND "movie_info_idx"."info_type_id" <= 99 AND "movie_info_idx"."info" LIKE '%7.%5%' AND "char_name"."id" IS NOT NULL AND "aka_name"."id" IS NOT NULL AND "kind_type"."id" IS NOT NULL AND "keyword"."id" IS NOT NULL;