SELECT MIN("info_type"."id") as agg_0, SUM("kind_type"."id") as agg_1 FROM "movie_info" LEFT OUTER JOIN "title" ON "movie_info"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "info_type" ON "movie_info_idx"."info_type_id" = "info_type"."id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id" LEFT OUTER JOIN "company_name" ON "movie_companies"."company_id" = "company_name"."id" LEFT OUTER JOIN "keyword" ON "movie_keyword"."keyword_id" = "keyword"."id"  WHERE "movie_companies"."note" NOT LIKE '%(Germany)%' AND "kind_type"."kind" IN ('video game', 'episode') AND "movie_info_idx"."info" LIKE '%6.9%' AND "company_name"."id" IS NOT NULL AND "keyword"."id" IS NOT NULL;