SELECT SUM("title"."season_nr") as agg_0, AVG("movie_companies"."movie_id") as agg_1 FROM "company_name" LEFT OUTER JOIN "movie_companies" ON "company_name"."id" = "movie_companies"."company_id" LEFT OUTER JOIN "company_type" ON "movie_companies"."company_type_id" = "company_type"."id" LEFT OUTER JOIN "title" ON "movie_companies"."movie_id" = "title"."id"  WHERE ("company_type"."kind" LIKE '%compani%es%' OR "company_type"."id" <= 2 OR "company_type"."kind" LIKE '%c%ompanies%') AND "company_name"."name" LIKE '%Pictures%' AND "company_name"."country_code" != '%[nz]%' AND "movie_companies"."movie_id" BETWEEN 1496425 AND 1975987;