SELECT MAX("title"."kind_id") as agg_0, MIN("movie_companies"."id") as agg_1 FROM "keyword" LEFT OUTER JOIN "movie_keyword" ON "keyword"."id" = "movie_keyword"."keyword_id" LEFT OUTER JOIN "title" ON "movie_keyword"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "company_name" ON "movie_companies"."company_id" = "company_name"."id" LEFT OUTER JOIN "company_type" ON "movie_companies"."company_type_id" = "company_type"."id"  WHERE "company_name"."country_code" != '%[sr]%' AND "company_name"."id" <= 125459 AND "company_name"."name" NOT LIKE '%Picture%s%' AND "movie_companies"."id" BETWEEN 1241856 AND 1295688 AND "title"."title" NOT LIKE '%of%' AND "company_type"."id" >= 2;