SELECT SUM("title"."production_year") as agg_0 FROM "cast_info" LEFT OUTER JOIN "aka_name" ON "cast_info"."person_id" = "aka_name"."id" LEFT OUTER JOIN "char_name" ON "cast_info"."person_role_id" = "char_name"."id" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id"  WHERE ("cast_info"."movie_id" >= 1199961 OR "cast_info"."movie_id" BETWEEN 892373 AND 1646125) AND "char_name"."id" <= 1758122 AND "aka_name"."id" >= 52173 AND ("title"."title" LIKE '%an%d%' OR ("title"."kind_id" != 7 AND "title"."title" LIKE '%de%')) AND "char_name"."surname_pcode" LIKE '%M5%';