SELECT SUM("movie_info_idx"."movie_id") as agg_0 FROM "company_name" LEFT OUTER JOIN "movie_companies" ON "company_name"."id" = "movie_companies"."company_id" LEFT OUTER JOIN "title" ON "movie_companies"."movie_id" = "title"."id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "company_type" ON "movie_companies"."company_type_id" = "company_type"."id"  WHERE "kind_type"."kind" IN ('tv series', 'video game', 'episode', 'movie', 'tv movie') AND "company_name"."name" LIKE '%o%f%' AND "movie_companies"."note" LIKE '%(2003)%' AND "company_type"."id" IS NOT NULL;